mapper.select:
SELECT IFNULL(MAX(chat_id), 0) + 1
FROM chat
WHERE chatroom_id = #{chatroomId}

SELECT COUNT(*) > 0
FROM chatroom_users
WHERE chatroom_id = #{chatroomId} AND user_id = #{usersId}

select chatroom_id
from chatroom_users
where user_id = #{userId}

select user_id
from chatroom_users
where chatroom_id = #{chatroomId}

select COUNT(*)
from chatroom_users
where chatroom_id = #{chatroomId}
