mapper.update:
UPDATE TripPlans
SET shares = shares+1, is_shared = 1
where id = #{planId};

mapper.select:
SELECT COUNT(*)
FROM TripPlans
WHERE id = #{planId} AND user_id = #{userId}

mapper.delete:
DELETE tpp FROM tripplanplaces tpp
INNER JOIN tripplandays tpd ON tpp.day_id = tpd.id
WHERE tpd.plan_id = #{planId}
